            if(data=="1") {
                alert("Запись успешно удалена."); window.close();
            } else {
                alert(data);
            }
               
        })
        .fail(function( e ){
           alert( e )     
        })
        
    };

    $('#bDelete').on('click', fn_delete); 
    
     $('#bClose').on('click', function () {
        if( window.confirm("Вы уверены?") ) window.close();
    });
   
     
})</script>> 

